import React from 'react'
import { useState } from 'react';
import './SigninPopup.css'
import LoginPopup from './LoginPopup'
import { faX } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import InputSchool from './InputSchool';
import { Link } from "react-router-dom";

function SigninPopup(props) {
    const [btnLoginPopup, setBtnLoginPopup] = useState(false);

    const [nombre, setNombre] = useState('');
    const getNombre = (event)=>{
        const userNombre = event.target.value;
        //console.log(userNombre); 
        setNombre(userNombre);
    };

    const [cedula, setCedula] = useState('');
    const getCedula = (event)=>{
        const userCedula = event.target.value;
        //console.log(userCedula);
        setCedula(userCedula); 
    };

    const [email, setEmail] = useState('');
    const getEmail = (event)=>{
        const userEmail = event.target.value;
        //console.log(userEmail);
        setEmail(userEmail); 
    };

    const [contra, setContra] = useState('');
    const getContra = (event)=>{
        const userContra = event.target.value;
        //console.log(userContra);
        setContra(userContra);

    };

    const [rol, setRol] = useState('');
    const getRol = (event)=>{
        const userRol = event.target.value;
        //console.log(userRol);
        setRol(userRol); 
    };

    function esEst(){
        if(rol==="Estudiante"){
            return true
        }else{return false}
    }
  return (props.trigger) ? (
    
    <div className='signin-popup'>
        
        <div className='signin-popup-inner'>
            {props.children}
            <form onSubmit={this.handleSubmit}>
                <table>
                    <tr>
                        <td><label htmlFor="name">Nombre: </label></td>
                        <td><input type="text" name="name" onChange={this.handleInputChange} value={this.state.name}/></td>
                    </tr>
                    <tr>
                        <td><label htmlFor="phone">Telefono: </label></td>
                        <td><input type="text" name="phone" onChange={this.handleInputChange} value={this.state.phone}/></td>
                    </tr>
                    <tr>
                        <td><label htmlFor="email">Correo: </label></td>
                        <td><input type="text" name="email" onChange={this.handleInputChange} value={this.state.email}/></td>
                    </tr>
                    <tr>
                        <td><label htmlFor="password">Contrase√±a: </label></td>
                        <td><input type="password" name="password" onChange={this.handleInputChange} value={this.state.password}/></td>
                    </tr>
                    <tr>
                        <td><label htmlFor="role">Rol: </label></td>
                        <td>
                            <select name="role" onChange={this.handleInputChange}>
                                <option value="student">Estudiante</option>
                                <option value="tutor">Tutor</option>
                            </select>
                        </td>
                    </tr>
                    <tr><br/></tr>
                    <tr>
                        <td> <input type="submit" value="Registrar"/> </td>
                    </tr>
                    </table>
                </form>
        </div>
        
    </div>
  ) : "";
}

export default SigninPopup
